#include <stdint.h>

// Defining the hex codes for various colors.
// Defined by Abhirath Koushik
#define WHITE       0xFFFF
#define LIGHT_BLUE  0x43bb
#define GREEN       0x07E0

// Defining the GPIO Pins for LCD, PB4 (BL), PB5 (DC), PB6 (CS), and PB7 (RST)
// Defined by Abhirath Koushik
#define GPIOB_BSRR_SET_DC   (1U << 5)      // Set PB5
#define GPIOB_BSRR_SET_CS   (1U << 6)      // Set PB6
#define GPIOB_BSRR_SET_RST   (1U << 7)      // Set PB7
#define GPIOB_BSRR_SET_PB4   (1U << 4)        // Set PB4

#define GPIOB_BSRR_RESET_DC (1U << (5 + 16))  // Reset PB5
#define GPIOB_BSRR_RESET_CS (1U << (6 + 16))  // Reset PB6
#define GPIOB_BSRR_RESET_RST (1U << (7 + 16))  // Reset PB7
#define GPIOB_BSRR_RESET_PB4 (1U << (4 + 16))  // Reset PB4

/*Defining the LCD Control Registers and Constant Codes.
  The code for these defines was originally written by Husamuldeen (Embedded Expert IO)*/
#define ST7789_SWRESET 0x01 // For LCD Reset
#define ST7789_SLPOUT  0x11 // For LCD out of Sleep Mode
#define ST7789_NORON   0x13 // For LCD Normal Display On
#define ST7789_INVON   0x21 // For LCD Inversion On
#define ST7789_DISPON  0x29 // For LCD Main Screen On
#define ST7789_CASET   0x2A // For LCD Column Address Set
#define ST7789_RASET   0x2B // For LCD Row Address Set
#define ST7789_RAMWR   0x2C // For LCD write to RAM
#define ST7789_COLMOD  0x3A // For LCD Set Color Mode
#define ST7789_MADCTL  0x36 // For LCD Memory Data Access Control
#define ST7789_COLOR_MODE_16bit 0x55    // For LCD RGB565 (16bit)

// Defines for LCD Resolution. Defined by Abhirath Koushik
#define LCD_WIDTH 240
#define LCD_HEIGHT 320

